{
                    /* Mobile Menu Button */}
                    <button className="md:hidden p-2" onClick={() => setIsOpen(!isOpen)}>
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            {isOpen ? (
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                            ) : (
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                            )}
                        </svg>
                    </button>

                    {/* Desktop Menu */}
                    <ul className="hidden md:flex space-x-4">
                        <li><Link to="/" className="hover:text-blue-200">Home</Link></li>
                        <li><Link to="/about" className="hover:text-blue-200">About</Link></li>
                        <li><Link to="/contact" className="hover:text-blue-200">Contact</Link></li>
                    </ul>

                    {/* Mobile Menu */}
                    <div className={`${isOpen ? 'block' : 'hidden'} md:hidden absolute top-16 left-0 right-0 bg-blue-600 p-4`}>
                        <ul className="flex flex-col space-y-2">
                            <li><Link to="/" className="block hover:text-blue-200" onClick={() => setIsOpen(false)}>Home</Link></li>
                            <li><Link to="/about" className="block hover:text-blue-200" onClick={() => setIsOpen(false)}>About</Link></li>
                            <li><Link to="/contact" className="block hover:text-blue-200" onClick={() => setIsOpen(false)}>Contact</Link></li>
                        </ul>
                    </div>
